const{src:src,dest:dest,watch:watch,series:series}=require("gulp"),sass=require("gulp-sass")(require("sass")),prefix=require("gulp-autoprefixer"),minify=require("gulp-clean-css"),terser=require("gulp-terser"),imagewebp=require("gulp-webp");function compilescss(){return src("*.scss").pipe(sass()).pipe(prefix("last 2 versions")).pipe(minify()).pipe(dest("dist/css"))}function jsmin(){return src("*.js").pipe(terser()).pipe(dest("dist/js"))}function webpImage(){return src("img/*.png").pipe(imagewebp()).pipe(dest("dist/images"))}function watchTask(){watch("*.scss",compilescss),watch("*.js",jsmin),watch("img/*.png",webpImage)}exports.default=series(compilescss,jsmin,webpImage);